<?php require_once('../control/issues.php');class XmlController extends IssuesController{	function XmlController(){		parent::IssuesController();				header("Content-type: text/xml"); 		echo "<?xml version=\"1.0\" encoding=\"UTF-8\"?>";	}}function xmlEscape($string) {    return str_replace(array('&', '<', '>', '\'', '"'), array('&amp;', '&lt;', '&gt;', '&apos;', '&quot;'), $string);}