<?phprequire ('../control/page.php'); $c = new PageController();$repo=$_GET['repo'];$issue_id=$_GET['ticket'];$action=$_GET['action'];$priority=isset($_GET['priority'])?$_GET['priority']:0;if($action=="increase"){	$c->db->upPriority($repo,$issue_id);	echo "increase $repo $issue_id";}else if($action=="increase5"){	$c->db->upPriority($repo,$issue_id);	$c->db->upPriority($repo,$issue_id);	$c->db->upPriority($repo,$issue_id);	$c->db->upPriority($repo,$issue_id);	$c->db->upPriority($repo,$issue_id);	echo "increase $repo $issue_id";}else if($action=="decrease"){	$c->db->downPriority($repo,$issue_id);	echo "decrease $repo $issue_id";}else if($action=="decrease5"){	$c->db->downPriority($repo,$issue_id);	$c->db->downPriority($repo,$issue_id);	$c->db->downPriority($repo,$issue_id);	$c->db->downPriority($repo,$issue_id);	$c->db->downPriority($repo,$issue_id);	echo "decrease $repo $issue_id";}else if($action=="bury"){	$c->db->bury($repo,$issue_id);	echo "buryed $repo $issue_id";}else if($action=="boost"){	$c->db->boost($repo,$issue_id);	echo "boosted $repo $issue_id";}else{	$c->db->updateIssuePriority($repo,$issue_id,$priority);	echo "updated $repo $issue_id to $priority";}?>