<?phprequire ('../control/page.php'); $c = new PageController();	$repo=$_GET['repo'];$issue_id=$_GET['ticket'];$action=$_GET['action'];$priority=$_GET['priority'];if($action=="increase"){	$c->db->increaseIssuePriority($repo,$issue_id);}else if($action=="decrease"){	$c->db->decreaseIssuePriority($repo,$issue_id);}else{	$c->db->updateIssuePriority($repo,$issue_id,$priority);}?>